import React from 'react';
import './ExpenseItem.css'
import {useState} from "react";
import {useEffect} from "react";
import './CartItem.css';

function CartItem(props) {

    const [ItemSum,setItemSum] = useState(props.price);
    const [Num,setNum] = useState(1);

    function AddNum (){
        setNum(Num+1);

        props.AddToSum(props.price);
        setItemSum(ItemSum+props.price);
    }
    function RemoveFromNum(){
       if(Num==1){props.removeFromCart(props.title,ItemSum)
           setItemSum(props.price);
       }else {
           setNum(Num-1);
           props.AddToSum(props.price*-1);
           setItemSum(ItemSum-props.price);
       }

    }

        return (
            <div>
                <button className="removeFromCart" onClick={()=>{props.removeFromCart(props.title,ItemSum)
                                        setItemSum(props.price);

                }}>Remove From Cart</button>
                <div className='expense-item'>
                    <div className=" expense-item__desc "> {props.title}
                        <button onClick={()=>{AddNum()}} className="button">+</button>
                        <button onClick={()=>{RemoveFromNum()}} className="button">-</button>
                     <h1>Quantity: {Num}</h1>
                        <div className="expense-item__price"> {props.price}<h7>â‚ª </h7></div></div>
                    <div ><img src={props.URL}  ></img></div>


                </div>
            </div>
        );
    }



export default CartItem;
