import React, {useEffect, useState} from 'react';
import ExpenseItem from "./components/ExpenseItem";
import milk from "./Images/milk.jpeg"
import eggs from  "./Images/eggs.jpeg"
import pizza from "./Images/pizza.jpg"
import oil from "./Images/oil.jpeg"
import choco from "./Images/choco.jpeg"
import "./App.css"
import CartItem from "./components/CartItem";







function App() {

  useEffect(()=>{
      fetch("http://localhost:8080/")
          .then((Response)=> Response.json()).then((data)=>console.log(data));
  })

  const [emptyCart, setCartEmpty] = useState(true);
  const [Cart,setCart] =  useState([]);
  const [SumPrice,setSumPrice] = useState(0);


  const expense= [
    {title: 'Milk',Price: 7  , URL: 'https://www.tnuva.co.il/uploads/f_606ee43fa87cf_1617880127.jpg',Shop:"Rami-Levi" ,Description: "Milk is a white liquid food produced by the mammary glands of mammals",src:milk },
    {title: 'Egg',Price: 12 , URL: 'https://ynet-images1.yit.co.il/picserver5/crop_images/2020/04/02/9886891/9886891_0_50_1000_563_0_x-large.jpg', Shop:"Supersal", Description: "The ultimate convenience food, eggs are powerhouses of nutrition packed with protein and a range of 18 vitamins and minerals.",src:eggs},
    {title: 'Pizza',Price: 20 , URL: 'https://www.kipa.co.il/userFiles/735-415/1_97fd5cc141db66addb84bd8f42f45e2b.jpg',Shop:"Osher Ad",Description: "Pizza is a dish of Italian origin consisting of a usually round, flat base of leavened wheat-based dough topped with tomatoes, cheese", src: pizza },
    {title: 'oil',Price: 8 , URL: 'https://beithashita.co.il/wp-content/uploads/2021/04/Canola-Oil.png',Shop:"Supersal",Description: "Cooking oil is plant, animal, or synthetic liquid fat used in frying, baking, and other types of cooking. It is also used in food preparation and flavoring" ,src: oil},
    {title: 'Chocolate',Price: 5, URL: 'https://images1.calcalist.co.il/picserver3/crop_images/2020/06/02/994931/994931_312_357_783_440_0_x-large.jpg',Shop:"Rami-Levi",Description: "Chocolate is a food made from roasted and ground cacao seed kernels that is available as a liquid, solid, or paste", src:choco },


  ];

  function addItemToCart(title,price,url,des,shop){
    const index = Cart.findIndex(item => item.title === title);
    if(index=== -1){
        const item = {title:title,Price:price,URL: url,Description: des,Shop:shop ,num: 1}
        setSumPrice(SumPrice+ price);
        setCart([...Cart,item]);
    }
    setCartEmpty(false);
   console.log(SumPrice);
  }

  function removeItemFromCart(title,price){
      setCart((current) =>
          current.filter((item) => item.title !== title)
      );
      if(Cart.length=== 1){
          setCartEmpty(true)
           setSumPrice(0);
      }else { setSumPrice(SumPrice- price);}


  }

  function AddToSum(price){
      setSumPrice(SumPrice+ price);
      console.log(SumPrice);
  }

 if(emptyCart) {return (
     <div>
       <h1 className="test">Items List </h1>
       <h2>Click on the items for more details</h2>
       {expense.map(item=>(
               <ExpenseItem
                   title={item.title}
                   price={item.Price}
                   URL={item.URL}
                   Description={item.Description}
                   src={item.src}
                   shop={item.Shop}
                   addItemToCart = {addItemToCart}
               ></ExpenseItem>
           )
       )}
     </div>
 );}else {return (
     <div>
       <div> <h1>Items List </h1>
         <h2>Click on the items for more details</h2>
         {expense.map(item=>(
                 <ExpenseItem
                     title={item.title}
                     price={item.Price}
                     URL={item.URL}
                     Description={item.Description}
                     src={item.src}
                     shop={item.Shop}
                     addItemToCart = {addItemToCart}
                 ></ExpenseItem>
             )
         )}</div>
    <div>
        <div>
            <h1>Cart List</h1>
            <h1>Total Price: {SumPrice}</h1>
        </div>

        {Cart.map(item=>(
          <CartItem
              title={item.title}
              price={item.Price}
              URL={item.URL}
              Description={item.Description}
              src={item.src}
              shop={item.Shop}
              num = {item.num}
              removeFromCart = {removeItemFromCart}
              AddToSum = {AddToSum}
          ></CartItem>

        ))}
    </div>


     </div>

 )}
}

export default App;
