import React from 'react';
import './ExpenseItem.css'
import {useState} from "react";

function ExpenseItem(props) {

    const [Clicked, setClicked] = useState(true);


   function save(title,price,url,des,shop){
       console.log("save");
       props.addItemToCart(title,price,url,des,shop);
   }


   if (Clicked){
       return (
         <div>
             <button className="addToCart" onClick={()=>{props.addItemToCart(props.title,props.price,props.URL,props.Description,props.shop)}}>Add to Cart</button>
           <div onClick={()=>setClicked(false)} className='expense-item'>
               <div className=" expense-item__desc "> {props.title}
                   <div className="expense-item__price"> {props.price}<h7>₪ </h7></div></div>
               <div ><img src={props.URL}  ></img></div>


           </div>
         </div>
       );
   }else {
       return (
<div>
    <button className="addToCart"   onClick={()=>{props.addItemToCart(props.title,props.price,props.URL,props.Description,props.shop)}}>Add to Cart</button>
           <div onClick={()=>setClicked(true)} className='expense-item'>

               <div ><img src={props.URL} width='120' hight='120' ></img></div>
               <div className="expense-item__price"> {props.price}<h7>₪ </h7></div>
               <div className="Description">{props.Description}
                    <h3>Shop: {props.shop}</h3>
               </div>

               <div ><img src={props.src}  ></img></div>
           </div>
</div>
       );
   }

}

export default ExpenseItem;
